try {
	return ##class(Telegram.Bot.Settings).CodeIndexOpen(code).Value
} catch ex {
	throw ex
}

try {
	set setting = ##class(Telegram.Bot.Settings).CodeIndexOpen(code)
	set setting.Value = Value
	do setting.%Save()
	return $$$OK
} catch ex {
	throw ex
}

set settings = [{"code":"ssl", "name":"SSL Sertificate", "value":""},
				{"code":"token", "name":"Token Telegram bot", "value":""},
				{"code":"last", "name":"Last updateId", "value":"0"},
				{"code":"local", "name":"Local update", "value":"0"},
				{"code":"interval", "name":"Interval server access", "value":"1"}]

for i = 0:1:settings.$size()-1 {
	if '$isobject(##class(Telegram.Bot.Settings).CodeIndexOpen(settings.$get(i)."code")) {
		set setting = ##class(Telegram.Bot.Settings).%New()
		set setting.Code = settings.$get(i)."code"
		set setting.Name = settings.$get(i)."name"
		set setting.Value = settings.$get(i)."value"
		do setting.%Save()

		w !, "Added property "_settings.$get(i)."code"
	}
	else {
		w !, "Property "_settings.$get(i)."code"_" already exists"
	}
}
